  resultText += `<b>–ü–æ—Å–ª–µ–¥–Ω–µ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ —Å—Ç–∞—Ç—É—Å–∞:</b> ${deadline}\n`;
  resultText += `------------------------------------\n`;
}

let count = filteredItems.length;

// –ü—Ä–∏–º–µ–Ω—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∫ –∫–∞–∂–¥–æ–º—É —ç–ª–µ–º–µ–Ω—Ç—É
for (const item of $input.all()) {

  item.json.result = resultText;
  item.json.counter = count;
}

return $input.all();
